    <script>
document.addEventListener("DOMContentLoaded", function () {
  var counters = document.querySelectorAll(".landing-page-counter");

  // Function to start the counter animation
  var startCounter = function (counter) {
    var target = parseFloat(counter.getAttribute("data-counter")); // Get the target value from data-counter attribute
    var step = target / 100; // Define how fast the counter increments
    var sign = counter.getAttribute("data-sign") || ""; // Get the optional sign (e.g., + or /5)
    var current = 0; // Start from 0

    // Update the counter on each interval
    var updateCounter = function () {
      if (current < target) {
        current += step; // Increment the current number
        // If it's an integer (no decimal), display it without decimals
        counter.textContent = addCommas(Math.round(current) === current ? current.toFixed(0) : current.toFixed(1)) + sign;
      } else {
        // Set to target value when finished
        counter.textContent = addCommas(Math.round(target) === target ? target.toFixed(0) : target.toFixed(1)) + sign;
        clearInterval(interval); // Stop the interval once we reach the target
      }
    };

    // Start an interval to increment the number
    var interval = setInterval(updateCounter, 10); // Update every 10ms for smooth animation
  };

  // Helper function to add commas to large numbers
  var addCommas = function (number) {
    return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  // Intersection Observer to start the counter animation when the element comes into view
  var observer = new IntersectionObserver(
    function (entries, observer) {
      entries.forEach(function (entry) {
        if (entry.isIntersecting) {
          startCounter(entry.target); // Start counter animation when the element is in view
          observer.unobserve(entry.target); // Stop observing once the animation starts
        }
      });
    },
    { threshold: 0.5 } // Trigger when 50% of the element is in view
  );

  // Observe all counter elements
  counters.forEach(function (counter) {
    observer.observe(counter);
  });
});

    </script>